    {{ attrs.render() }}
&gt;
  &lt;div class="pb-4 pt-0"&gt;{{ content }}&lt;/div&gt;
&lt;/div&gt;
</code></pre> <pre><code>{#def
    value: str = "",
    className: str = ""
#}
